    <script>
        $(document).ready(function() {
            const lastPage = `{{ $offenders->lastPage() }}`;
            var page = 1;
            $(window).scroll(function() {
                if (page < lastPage && lastPage > 1) {
                    if ($(window).scrollTop() + $(window).height() >= $(document).height()) {
                        page++;
                        loadMoreData(page);
                    }
                }
            });


            function loadMoreData(page) {
                $.ajax({
                        url: '?page=' + page + '&kasus={{ request("kasus") }}',
                        type: "get",
                        beforeSend: function() {
                            $('#loading').show();
                        },
                    })
                    .done(function(data) {
                        let detailData = ``;
                        data.data.map((el, i) => {
                            detailData += `
                            <div class="card shadow mt-5">
                                <div class="card-body">
                                    <div class="d-flex justify-content-between">
                                        <div class="d-flex">
                                            <svg width="57" height="57" viewBox="0 0 57 57" fill="none" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink">
                                                <rect width="57" height="57" rx="18" fill="#F8F8F8" />
                                                <rect x="12.5" y="12.5" width="32" height="32" fill="url(#pattern0)" />
                                                <defs>
                                                    <pattern id="pattern0" patternContentUnits="objectBoundingBox" width="1" height="1">
                                                        <use xlink:href="#image0_10890_5546" transform="scale(0.00195312)" />
                                                    </pattern>
                                                    <image id="image0_10890_5546" width="512" height="512" xlink:href="data:image/png;base64," />
                                                </defs>
                                            </svg>
                                            <div style="margin-left: 10px;margin-top:10px;">
                                                <h3>${ el.crime_key == 'App/Model/MasterListCrimeOfPotentialTarget' ? '-' : '' }</h3>
                                                <p>${ el.created_at_formatted }</p>
                                            </div>
                                        </div>
                                        <div class="mt-5">
                                            <div class=${el.status == 1 ? "pendaftaran" : el.status == 2 ? "tindakan" : "tutup"}>${el.status == 1 ? `Pendaftaran` : el.status == 2 ? "Tindakan" : "Ditutup"}</div>
                                        </div>
                                    </div>
                                    <div class="row mt-5">
                                        <div class="col-6">
                                            <h3>Alat</h3>
                                            <p>${ el.offender_crime_indication?.equipment?.name ?? '-' }</p>
                                        </div>
                                        <div class="col-6">
                                            <h3>Motif</h3>
                                            <p>${ el.offender_crime_indication?.motive?.name ?? '-' }</p>
                                        </div>
                                    </div>
                                    <div class="row">
                                        <div class="col-6 mr-auto">
                                            <h3>Jenis Kasus</h3>
                                            <p>${ el.type_formatted }</p>
                                        </div>
                                    </div>
                                    <div class="row">
                                        <div class="col-6">
                                            <h5>Sambang Internal</h5>
                                            <p>${ el.offender_internal_visit?.date_visit ?? '-' }</p>
                                        </div>
                                        <div class="col-6">
                                            <h5>Sambang Eksternal</h5>
                                            <p>${ el.offender_external_visit?.date_visit ?? '-' }</p>
                                        </div>
                                    </div>
                                </div>
                            </div>`;
                        });
                        $("#data-content").append(detailData);
                        $('#loading').hide();
                    })
                    .fail(function(jqXHR, ajaxOptions, thrownError) {
                        alert('server not responding...');
                    });
            }

            function formatDate(date) {
                const today = new Date(date);
                const yyyy = today.getFullYear();
                let mm = today.getMonth() + 1; // Months start at 0!
                let dd = today.getDate();

                if (dd < 10) dd = '0' + dd;
                if (mm < 10) mm = '0' + mm;

                return dd + '/' + mm + '/' + yyyy;
            }
        });
    </script>
